INSERT INTO MOVIES (id_movie, name, description, release_year, genres, votes, id_image_movie) VALUES (3317, 'Oppenheimer', 'During World War II, Lt. Gen. Leslie Groves Jr. appoints physicist J. Robert Oppenheimer to work on the top-secret Manhattan Project. Oppenheimer and a team of scientists spend years developing and designing the atomic bomb. Their work comes to fruition on July 16, 1945, as they witness the worlds first nuclear explosion, forever changing the course of history.', '2023-07-20', 'Suspense', 0, 7722);
INSERT INTO MOVIES (id_movie, name, description, release_year, genres, votes, id_image_movie) VALUES (3318, 'Barbie', 'Barbie and Ken are having the time of their lives in the colorful and seemingly perfect world of Barbie Land. However, when they get a chance to go to the real world, they soon discover the joys and perils of living among humans.', '2023-07-21', 'Comedy', 0, 7723);
INSERT INTO MOVIES (id_movie, name, description, release_year, genres, votes, id_image_movie) VALUES (3319, 'M3GAN', 'M3GAN is a marvel of artificial intelligence, a lifelike doll thats programmed to be a childs greatest companion and a parents greatest ally. Designed by Gemma, a brilliant roboticist, M3GAN can listen, watch and learn as it plays the role of friend and teacher, playmate and protector. When Gemma becomes the unexpected caretaker of her 8-year-old niece, she decides to give the girl an M3GAN prototype, a decision that leads to unimaginable consequences.', '2023-01-04', 'Horror', 5, 7724);
INSERT INTO MOVIES (id_movie, name, description, release_year, genres, votes, id_image_movie) VALUES (3320, 'Creed III', 'Still dominating the boxing world, Adonis Creed is thriving in his career and family life. When Damian, a childhood friend and former boxing prodigy resurfaces after serving time in prison, hes eager to prove that he deserves his shot in the ring. The face-off between former friends is more than just a fight. To settle the score, Adonis must put his future on the line to battle Damian -- a fighter who has nothing to lose.', '2023-02-09', 'Action', 6, 7725);
INSERT INTO MOVIES (id_movie, name, description, release_year, genres, votes, id_image_movie) VALUES (3321, 'Scream VI', 'Four survivors of the Ghostface murders leave Woodsboro behind for a fresh start in New York City. However, they soon find themselves in a fight for their lives when a new killer embarks on a bloody rampage.', '2023-03-10', 'Horror', 0, 7726);
INSERT INTO MOVIES (id_movie, name, description, release_year, genres, votes, id_image_movie) VALUES (3322, 'Wonka', 'Armed with nothing but a hatful of dreams, young chocolatier Willy Wonka manages to change the world, one delectable bite at a time.', '2023-12-08', 'Adventure', 0, 7727);
INSERT INTO MOVIES (id_movie, name, description, release_year, genres, votes, id_image_movie) VALUES (3323, 'Cocaine Bear', 'A 227kg black bear consumes a significant amount of cocaine and embarks on a drug-fuelled rampage through a Georgia forest, endangering the lives of cops, criminals, tourists and teenagers.', '2023-02-24', 'Comedy', 10, 7728);
INSERT INTO MOVIES (id_movie, name, description, release_year, genres, votes, id_image_movie) VALUES (3324, 'Indiana Jones 5', 'Daredevil archaeologist Indiana Jones races against time to retrieve a legendary dial that can change the course of history. Accompanied by his goddaughter, he soon finds himself squaring off against JÃ¼rgen Voller, a former Nazi who works for NASA.', '2023-06-28', 'Adventure', 0, 7729);
INSERT INTO MOVIES (id_movie, name, description, release_year, genres, votes, id_image_movie) VALUES (3325, 'The Batman', 'Batman ventures into Gotham Citys underworld when a sadistic killer leaves behind a trail of cryptic clues. As the evidence begins to lead closer to home and the scale of the perpetrators plans become clear, he must forge new relationships, unmask the culprit and bring justice to the abuse of power and corruption that has long plagued the metropolis.', '2022-03-01', 'Action', 8, 7730);
INSERT INTO MOVIES (id_movie, name, description, release_year, genres, votes, id_image_movie) VALUES (3326, 'Jurassic World Dominion', 'Four years after the destruction of Isla Nublar, dinosaurs now live and hunt alongside humans all over the world. This fragile balance will reshape the future and determine, once and for all, whether human beings are to remain the apex predators on a planet they now share with historys most fearsome creatures.', '2022-06-10', 'Action', 0, 7731);
INSERT INTO MOVIES (id_movie, name, description, release_year, genres, votes, id_image_movie) VALUES (3327, 'Morbius', 'Dangerously ill with a rare blood disorder and determined to save others from the same fate, Dr. Morbius attempts a desperate gamble. While at first it seems to be a radical success, a darkness inside of him is soon unleashed.', '2022-03-10', 'Action', 0, 7732);
INSERT INTO MOVIES (id_movie, name, description, release_year, genres, votes, id_image_movie) VALUES (3328, 'Prey', 'A skilled Comanche warrior protects her tribe from a highly evolved alien predator that hunts humans for sport, fighting against wilderness, dangerous colonisers and this mysterious creature to keep her people safe.', '2022-08-05', 'Action', 0, 7733);
INSERT INTO MOVIES (id_movie, name, description, release_year, genres, votes, id_image_movie) VALUES (3329, 'Sonic the Hedgehog 2', 'After settling in Green Hills, Sonic is eager to prove that he has what it takes to be a true hero. His test comes when Dr. Robotnik returns with a new partner, Knuckles, in search of a mystical emerald that has the power to destroy civilizations. Sonic teams up with his own sidekick, Tails, and together they embark on a globe-trotting journey to find the emerald before it falls into the wrong hands.', '2022-04-01', 'Action', 0, 7734);
INSERT INTO MOVIES (id_movie, name, description, release_year, genres, votes, id_image_movie) VALUES (3330, 'The Woman King', 'Inspired by real events, it follows the epic journey of General Nanisca (Viola Davis), who trains a new generation of recruits and prepares them for battle against an enemy determined to destroy their way of life.', '2022-09-16', 'Drama', 0, 7735);
INSERT INTO MOVIES (id_movie, name, description, release_year, genres, votes, id_image_movie) VALUES (3331, 'Halloween Ends', 'Four years after her last encounter with masked killer Michael Myers, Laurie Strode is living with her granddaughter and trying to finish her memoir. Myers hasnt been seen since, and Laurie finally decides to liberate herself from rage and fear and embrace life. However, when a young man stands accused of murdering a boy that he was babysitting, it ignites a cascade of violence and terror that forces Laurie to confront the evil she cant control.', '2022-10-11', 'Horror', 7, 7736);
INSERT INTO MOVIES (id_movie, name, description, release_year, genres, votes, id_image_movie) VALUES (3332, 'Dont Worry Darling', 'In the 1950s, Alice and Jack live in the idealized community of Victory, an experimental company town that houses the men who work on a top-secret project. While the husbands toil away, the wives get to enjoy the beauty, luxury and debauchery of their seemingly perfect paradise. However, when cracks in her idyllic life begin to appear, exposing flashes of something sinister lurking below the surface, Alice cant help but question exactly what shes doing in Victory.', '2022-09-23', 'Suspense', 0, 7737);
